// HEAD FILE

// DOM Objects

const mainScreen = document.querySelector(".main-screen");
const pokeName = document.querySelector(".poke-name");
const pokeId = document.querySelector(".poke-id");

const pokeFrontImage = document.querySelector(".poke-front-image");
const pokeBackImage = document.querySelector(".poke-back-image");

const pokeTypeOne = document.querySelector(".poke-type-one");
const pokeTypeTwo = document.querySelector(".poke-type-two");

const pokeWeight = document.querySelector(".poke-weight");
const pokeHeight = document.querySelector(".poke-height");

const leftButton = document.querySelector(".left-button");
const rightButton = document.querySelector(".right-button");
const pokeListItems = document.querySelectorAll(".list-item");

const powerButton = document.querySelector(".top-section__blue");

// global constants and variables
const POKEID_KEY = "data-pokeID";
let prevURL = null;
let nextURL = null;

// Functions
const capitalize = str =>
  str[0].toUpperCase() + str.slice(1);

const pad0format = num => num.toString().padStart(3, "0");

function resetScreen() {
  mainScreen.classList.remove("hide");
  const classes = mainScreen.classList;
  classes.remove(...classes);
}

function fetchPokeList(url) {
  fetch(url)
    .then(res => res.json())
    .then(data => {
      const { results, previous, next } = data;
      prevURL = previous;
      nextURL = next;

      pokeListItems.forEach((pokeItem, i) => {
        const resultData = results[i];

        if (resultData) {
          const { name, url } = resultData;
          const urlArray = url.split("/");
          const [id] = urlArray.slice(-2);

          pokeItem[POKEID_KEY] = id;


          pokeItem.textContent = (id) + ". " + capitalize(name);
        } else {
          pokeItem.textContent = "";
        }
      });
    });
}

function fetchAPokemon(nameOrId) {
  fetch("https://pokeapi.co/api/v2/pokemon/" + nameOrId)
    .then(res => res.json())
    .then(data => {
      resetScreen();

      pokeName.textContent = capitalize(data.name);
      pokeId.textContent = "#" + pad0format(data.id);
      pokeWeight.textContent = data.weight;
      pokeHeight.textContent = data.height;

      const [dataFirstType, dataSecondType] = data.types;

      const firstTypeName = dataFirstType.type.name;
      pokeTypeOne.textContent = capitalize(firstTypeName);

      mainScreen.classList.add(firstTypeName);

      if (dataSecondType) {
        pokeTypeTwo.classList.remove("hide");
        const secondTypeName = dataSecondType.type.name;
        pokeTypeTwo.textContent = capitalize(secondTypeName);

      } else {
        pokeTypeTwo.classList.add("hide");
        pokeTypeTwo.textContent = "";

      }

      pokeFrontImage.src = data.sprites.front_default || "";
      pokeBackImage.src = data.sprites.back_default || "";

    });
}

function handleLeftBtnClick(ev) {
  beep();
  if (prevURL) {
    fetchPokeList(prevURL);
  }
}

function handleRightBtnClick(ev) {
  beep();
  if (nextURL) {
    fetchPokeList(nextURL);
  }
}

function handlePokeItemClick(ev) {
  beep();
  const pokeItem = ev.target;
  if (pokeItem && pokeItem.textContent) {
    const id = pokeItem[POKEID_KEY];
    fetchAPokemon(id);
  }
}


function beep() {
  var snd = new Audio("data:audio/wav;base64,");
  snd.play();
}

// adding event listeners

leftButton.addEventListener("click", handleLeftBtnClick);
rightButton.addEventListener("click", handleRightBtnClick);

for (const pokeItem of pokeListItems) {
  pokeItem.addEventListener("click", handlePokeItemClick);
}

powerButton.addEventListener("click", ev => {
  mainScreen.classList.toggle("hide");
});


// initialize App

fetchPokeList("https://pokeapi.co/api/v2/pokemon?offset=0&limit=20");

// ------------ END OF FILE - EOF ------------ //